package engine

import (
	"strings"
	"testing"

	"github.com/hexops/autogold/v2"
)

func TestCompressEnv(t *testing.T) {
	cases := []struct {
		name   string
		envs   []string
		expect autogold.Value
	}{
		{
			name:   "EmptyEnv",
			envs:   []string{},
			expect: autogold.Expect([]string{}),
		},
		{
			name:   "SmallValue",
			envs:   []string{"ENV=12345"},
			expect: autogold.Expect([]string{"ENV=12345"}),
		},
		{
			name:   "LargeValue",
			envs:   []string{"ENV=" + strings.Repeat("1", 40001)},
			expect: autogold.Expect([]string{`ENV={"_gz":"H4sIAAAAAAAA/+zAwQAAAAACMab85XqexDYAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACAPAAD//2wBriFBnAAA"}`}),
		},
		{
			name:   "LargeValue1",
			envs:   []string{"ENV=" + strings.Repeat("1", 40000)},
			expect: autogold.Expect([]string{`ENV={"_gz":"H4sIAAAAAAAA/+zAwQAAAAACMab85fodxTYAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACAPAAD//yOpnxVAnAAA"}`}),
		},
		{
			name:   "LargeValue2",
			envs:   []string{"ENV=" + strings.Repeat("1", 39999)},
			expect: autogold.Expect([]string{""}),
		},
		{
			name:   "MultipleValues",
			envs:   []string{"A=1", "B=2"},
			expect: autogold.Expect([]string{"A=1", "B=2"}),
		},
		{
			name:   "MultipleValuesWithLarge",
			envs:   []string{"A=1", "B=2", "C=" + strings.Repeat("3", 40001)},
			expect: autogold.Expect([]string{"A=1", "B=2", `C={"_gz":"H4sIAAAAAAAA/+zAwQAAAAACMaj83XqexDYAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACAPAAD//1gf9UpBnAAA"}`}),
		},
	}

	for _, tc := range cases {
		t.Run(tc.name, func(t *testing.T) {
			result := compressEnv(tc.envs)
			tc.expect.Equal(t, result)
		})
	}
}
